CREATE NODE TABLE IMDB_LANGUAGE_CODE(alpha2 STRING, English STRING, PRIMARY KEY (alpha2));
CREATE NODE TABLE IMDB_GENRE(genre STRING, PRIMARY KEY (genre));
CREATE NODE TABLE IMDB_DISTRIBUTION_TYPE(distribution_type STRING, PRIMARY KEY (distribution_type));
CREATE NODE TABLE IMDB_JOB_CATEGORY(category STRING, PRIMARY KEY (category));
CREATE NODE TABLE IMDB_TITLE_TYPE(title_type STRING, PRIMARY KEY (title_type));
CREATE NODE TABLE IMDB_ATTRIBUTE(attribute STRING, PRIMARY KEY (attribute));
